export function macOSHeader(parent: any, appearance: boolean, title: string, style) {
	return (
		<Frame
			style={{
				height: 22,
				background: appearance
					? 'linear-gradient(#4B4B4B, #3D3D3D)'
					: 'linear-gradient(#EDECED, #D2D1D2)',
				boxShadow: appearance
					? 'inset 0 -1px rgba(0,0,0,1), inset 0 -1px rgba(43,43,43,1), inset 0 -1px rgba(255,255,255,0.01)'
					: 'inset 0 1px 1px rgba(255,255,255,0.5), inset 0 -1px rgba(183,180,183,1), inset 0 -1px rgba(183,180,183,0.3)',
				borderRadius: '4px 4px 0 0',
				color: appearance ? 'rgba(255,255,255,.85)' : 'rgba(0,0,0,0.73)',
				width: '100%'
			}}
			onPan={parent.panHeaderHandler}
			onPanStart={parent.panHeaderStartHandler}
			top={0}
			left={0}>
			<div
				style={{
					width: '100%',
					height: 22,
					display: 'flex',
					alignItems: 'center',
					justifyContent: 'space-between',
					whiteSpace: 'nowrap',
					textAlign: 'left',
					padding: 8,
					overflow: 'hidden'
				}}>
				<div style={trafficLightsWrapper}>
					<div style={trafficLight('#FF6157', '#E24640', appearance, style)} />
					<div style={trafficLight('#FFC12F', '#DFA023', appearance, style)} />
					<div style={trafficLight('#2ACB42', '#1BAC2C', appearance, style)} />
				</div>
				<div style={{ fontSize: 13 }}>{title}</div>
				<div style={spacer(52)} />
			</div>
		</Frame>
	);
}

export function noHeader(parent: any, height: number) {
	return (
		<Frame
			style={{
				height: height,
				background: null,
				borderRadius: '4px 4px 0 0',
				width: '100%',
				paddingTop: 0
			}}
			onPan={parent.panHeaderHandler}
			onPanStart={parent.panHeaderStartHandler}
			top={0}
			left={0}
		/>
	);
}

const trafficLightsWrapper: React.CSSProperties = {
	width: 52,
	flexShrink: 0,
	height: '100%',
	display: 'flex',
	alignItems: 'center',
	justifyContent: 'space-between'
};

function trafficLight(
	background: string,
	borderColor: string,
	appearance: boolean,
	style: string
): React.CSSProperties {
	return {
		width: 12,
		height: 12,
		background,
		borderRadius: '50%',
		borderColor: borderColor,
		borderStyle: 'solid',
		borderWidth: appearance ? (style === 'macOS' || style === 'Safari' ? 0 : 0.5) : 0.5
	};
}

function spacer(width: number): React.CSSProperties {
	return {
		width: width
	};
}

